/* Effek dari operator ++ */
public class oop_kode11 {
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* Kamus */
		int i, j;

		/* Program */
		i = 3;
		j = i++;

		System.out.println ("Nilai i : " + (++i) +
						"\nNilai j : " + j);

	}
}
